package com.javiereduardo.recilerviewpersona

import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView

class MainActivity : AppCompatActivity() {
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)

        val listaDeLugares = ArrayList<Lugar>()
        listaDeLugares.add(Lugar("Ameca", "https://th.bing.com/th/id/OIP.gY5gdQwNMBeOjqpDYT7R1wHaE6?w=296&h=196&c=7&o=5&pid=1.7", "Vendo picones"))
        listaDeLugares.add(Lugar("Villa", "data:image/jpeg;base64,", "Vendo Ponche"))
        listaDeLugares.add(Lugar("Acatlan", "data:image/jpeg;base64,", "Vendo Cacahuates"))
        listaDeLugares.add(Lugar("Tequila", "https://th.bing.com/th/id/OIP.ye9snk3GHi6PHhpXzLYGewHaFz?w=222&h=180&c=7&o=5&pid=1.7", "Vendo Tequila"))
        listaDeLugares.add(Lugar("Zacoalco", "https://th.bing.com/th/id/OIP.w1cWyOU1jRbJ_tbteq3KHAHaE7?w=284&h=187&c=7&o=5&pid=1.7", "Vendo Equipales"))
        listaDeLugares.add(Lugar("Puebla", "https://th.bing.com/th/id/OIP.ZpyauntPl2pcy2NUl-eUXAHaE3?w=267&h=180&c=7&o=5&pid=1.7", "Vendo Camotes en su mole"))

        var recyclerview:RecyclerView
        recyclerview = findViewById(R.id.recyclerview)
        recyclerview.layoutManager = LinearLayoutManager(this, LinearLayoutManager.VERTICAL, false)
        recyclerview.setHasFixedSize(true)
        recyclerview.adapter = LugarAdapter(listaDeLugares, this)
    }
}